<?php

require __DIR__.'/vendor/autoload.php';

$app = require_once __DIR__.'/bootstrap/app.php';
$app->make('Illuminate\Contracts\Console\Kernel')->bootstrap();

use Illuminate\Support\Facades\DB;

echo "╔════════════════════════════════════════════════════════════════╗\n";
echo "║   UPDATING PRODUCTS & UMKM WITH BETTER QUALITY IMAGES          ║\n";
echo "╚════════════════════════════════════════════════════════════════╝\n\n";

// Better quality placeholder images (100x100 pixels)
$productImages = [
    'kue' => 'data:image/jpeg;base64,',
    'batik' => 'data:image/jpeg;base64,',
    'kerajinan' => 'data:image/jpeg;base64,',
    'kopi' => 'data:image/jpeg;base64,',
    'tas' => 'data:image/jpeg;base64,',
];

$tokoImage = 'data:image/jpeg;base64,';

echo "STEP 1: Updating UMKM Photos\n";
echo "═════════════════════════════\n\n";

$umkms = DB::table('tumkm')->get();
$umkmUpdated = 0;

foreach ($umkms as $umkm) {
    DB::table('tumkm')
        ->where('id', $umkm->id)
        ->update(['foto_toko' => $tokoImage]);
    
    echo "✓ {$umkm->nama_toko}\n";
    $umkmUpdated++;
}

echo "\nSTEP 2: Updating Product Images\n";
echo "══════════════════════════════\n\n";

$products = DB::table('tproduk')->get();
$productUpdated = 0;

foreach ($products as $product) {
    // Determine image type based on category
    $imageType = 'kue'; // default
    
    if (stripos($product->kategori, 'Fashion') !== false || stripos($product->nama_produk, 'Batik') !== false || stripos($product->nama_produk, 'Tas') !== false) {
        if (stripos($product->nama_produk, 'Tas') !== false) {
            $imageType = 'tas';
        } else {
            $imageType = 'batik';
        }
    } elseif (stripos($product->kategori, 'Kerajinan') !== false || stripos($product->nama_produk, 'Bambu') !== false) {
        $imageType = 'kerajinan';
    } elseif (stripos($product->kategori, 'Minuman') !== false || stripos($product->nama_produk, 'Kopi') !== false) {
        $imageType = 'kopi';
    } elseif (stripos($product->kategori, 'Makanan') !== false || stripos($product->nama_produk, 'Kue') !== false) {
        $imageType = 'kue';
    }
    
    DB::table('tproduk')
        ->where('id', $product->id)
        ->update(['gambar' => $productImages[$imageType]]);
    
    echo "✓ {$product->nama_produk} ({$imageType})\n";
    $productUpdated++;
}

echo "\n╔══════════════════════════════════════════════════╗\n";
echo "║              UPDATE SUMMARY                      ║\n";
echo "╠══════════════════════════════════════════════════╣\n";
echo "║  UMKM Updated: " . str_pad($umkmUpdated, 31) . "║\n";
echo "║  Products Updated: " . str_pad($productUpdated, 27) . "║\n";
echo "╚══════════════════════════════════════════════════╝\n\n";

echo "✅ All images have been updated successfully!\n";
